mapper.update:
UPDATE members
SET user_id = #{user_id},
    team_id = #{team_id},
    status = #{status},
    inviter_id = #{inviter_id}
WHERE id = #{id};

mapper.select:
SELECT m.user_id, u.name
FROM members m JOIN users u ON m.user_id = u.id
WHERE team_id = #{team_id};

SELECT m.team_id, t.team_name
FROM members m JOIN teams t
    ON m.team_id = t.id
WHERE user_id = #{userId}
  AND status = 0;
